function assignScrollToTop(){$("a.pagination-item.current").click(function(){$("html,body").animate({scrollTop:0},"slow")})}function getAnalyticsData(){$(".post-views").count>0||$.ajax({url:"https://garys-zone.appspot.com/query?id=agxzfmdhcnlzLXpvbmVyFQsSCEFwaVF1ZXJ5GICAgICAgIAKDA",dataType:"jsonp",timeout:3e3,success:function(n){processPageView(n.rows)},error:function(){$.ajax({url:"/assets/json/pageviews.json",dataType:"json",success:function(n){console.log("Local pageviews data used");processPageView(n.rows)}})}})}function processPageView(n){n!==undefined&&$(".archive-post-title").each(function(){var i=$(this).attr("href"),r,t;if(i)for(r=n.length,t=0;t<r;t++)n[t][0]===i&&$(this).siblings(".post-excerpt").children(".post-meta").append('<div class="post-views"><span class="fa fa fa-eye"><\/span> '+n[t][1]+" <\/div>")})}$(function(){assignScrollToTop();getAnalyticsData()})